import React, { useEffect, useState } from "react";
import "./style.css";
import { Row, Col } from "react-bootstrap";
import { getFaceSkin } from '../apis/faceSkinApi'



const BadLivingHabit = ({ inputHabitValue, setInputHabitValue }) => {
  const [faceSkins, setfaceSkins] = useState([])
  const handleInputHabitValue = (e) => {
    const habitArr = inputHabitValue.habit.map((el) => el);
    habitArr.push(e.target.value);

    setInputHabitValue({
      ...inputHabitValue,
      [e.target.name]: habitArr,
    });
  };

  const handleCheckValue = (item) => (cb) => {
    const habitArr = inputHabitValue.habit.map((el) => el);
    if(inputHabitValue?.habit?.includes(item.description)) {
       habitArr = habitArr?.filter((value)=> value!==item.description)
    } else habitArr.push(item.de);
    setInputHabitValue({
      ...inputHabitValue,
      [cb.target.name]: habitArr,
    });
  }

  useEffect(() => {
    getFaceSkin().then((res)=> {
      setfaceSkins(res.data)
    })
  }, [inputHabitValue]);

  return (
    <div className="bad-living-habit big-part">
      <label className="first-label" htmlFor="bad-living-habit">
        Triệu chứng trên da mặt:
      </label>
      <Row>
        {faceSkins.map((item, index) => (
          <Col xs={6} md={4} xxl={3} key={index}>
            <input
              onClick={handleCheckValue(item)}
              onChange={(e) => handleInputHabitValue(e)}
              type="checkbox"
              id={`habit${index + 1}`}
              name="habit"
              value={`bad-habit${index + 1}`}
            />
            <label htmlFor={`habit${index + 1}`}>{item.description}</label>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default BadLivingHabit;
