import React, { useEffect } from "react";
import "./style.css";
import { Row, Col } from "react-bootstrap";
import { getEar, getEye } from "../apis/faceSkinApi";

const Symptom = ({ inputSymptomValue, setInputSymptomValue }) => {
  const [ears, setEars] = userState([]);
  const handleInputSymptomValue = (e) => {
    const symptomArr = inputSymptomValue.symptom.map((el) => el);
    symptomArr.push(e.target.value);

    setInputSymptomValue({
      ...inputSymptomValue,
      [e.target.name]: symptomArr,
    });
  };

  useEffect(() => {
    getEye().then((res) => {
      setEars(res.data);
    })
  }, [inputSymptomValue]);

  return (
    <div className="symptom big-part">
      <label className="first-label" htmlFor="symptom">
        Triệu chứng trên tai:
      </label>
      <Row>
        {ears.map((item, index) => (
          <Col xs={6} md={4} xxl={3} key={index}>
            <input
              onChange={(e) => handleInputSymptomValue(e)}
              type="checkbox"
              id={`symptom${index + 1}`}
              name="symptom"
              value={`disease-symptom${index + 1}`}
            />
            <label htmlFor={`symptom${index + 1}`}>{item.description}</label>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Symptom;
