import React, { useEffect } from "react";
import "./style.css";
import { Row, Col } from "react-bootstrap";

const causeMapArr = [
  {
    "id": 21,
    "description": "Mắt đỏ",
    "createdAt": "2022-01-14T08:28:34.308Z",
    "updatedAt": "2022-01-14T08:28:34.308Z",
    "type": 1
},
{
    "id": 22,
    "description": "Lộn mi(chỉ tình trạng cuộn vào trong của bờ mi vào nhãn cầu)",
    "createdAt": "2022-01-14T08:28:44.301Z",
    "updatedAt": "2022-01-14T08:28:44.301Z",
    "type": 1
},
{
    "id": 23,
    "description": "Ở mí mắt: thương tổn thường phù nề, kết hợp với viêm kết mạc.",
    "createdAt": "2022-01-14T08:28:52.058Z",
    "updatedAt": "2022-01-14T08:28:52.058Z",
    "type": 1
},
{
    "id": 24,
    "description": "Xuất hiện những u thịt nhỏ trên những vùng da như vùng mặt, mí mắt",
    "createdAt": "2022-01-14T08:29:00.573Z",
    "updatedAt": "2022-01-14T08:29:00.573Z",
    "type": 1
},
{
    "id": 25,
    "description": "Sưng phù",
    "createdAt": "2022-01-14T08:29:09.179Z",
    "updatedAt": "2022-01-14T08:29:09.179Z",
    "type": 1
}
];

const Cause = ({ inputCauseValue, setInputCauseValue }) => {
  const handleInputCauseValue = (e) => {
    const causeArr = inputCauseValue.cause.map((el) => el);
    causeArr.push(e.target.value);

    setInputCauseValue({
      ...inputCauseValue,
      [e.target.name]: causeArr,
    });
  };

  useEffect(() => {
    console.log("InputCauseValue: " + inputCauseValue.cause);
  }, [inputCauseValue]);

  return (
    <div className="cause big-part">
      <label className="first-label" htmlFor="cause">
        Triệu chứng trên mắt:
      </label>
      <Row>
        {causeMapArr.map((item, index) => (
          <Col xs={6} md={4} xxl={3} key={index}>
            <input
              onChange={(e) => handleInputCauseValue(e)}
              type="checkbox"
              id={`cause${index + 1}`}
              name="cause"
              value={`reason${index + 1}`}
            />
            <label htmlFor={`cause${index + 1}`}> {item}</label>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default Cause;
